import "./TodoItem.css";

function TodoItem({ text, completado }) {
  return (
    <>
      <span className={` ${completado && "Icon-check--active"} `}>V</span>
      <p className={` ${completado && "TodoItem-p--complete"} `}>
        V {text}, Completado: {completado ? "Completado" : "Sin Completar"} X
      </p>
    </>
  );
}
export { TodoItem };
