import logo from "./platzi.webp";
import "./App.css";

import { TodoCounter } from "./TodoCounter";
import { TodoFilter } from "./TodoFilter";
import { TodoList } from "./TodoList";
import { TodoItem } from "./TodoItem";
import { CreateTodoButton } from "./CreateTodoButton";

const defaultTodos = [
  { key: 1, text: "Cortar Cebolla", completed: false },
  { key: 2, text: "Definir Props", completed: true },
  { key: 3, text: "Aprender React", completed: false },
  { key: 4, text: "Elemento Sin completar", completed: true },
];

function App() {
  return (
    <>
      <TodoCounter completed={10} total={20} />
      <TodoFilter />
      <TodoList>

        {defaultTodos.map((todo) => (
          <TodoItem key={todo.key} text={todo.text} completado={todo.completed}  />
        ))}

        {/* Tambiem se podría hacer de esta manera, con un retur, sin embargo se reemplazan los {} por () y no hay necesidad de hacer return, como está arriba */}

        {/*defaultTodos.map(() => {
          return <TodoItem />;
        })*/
        }


      </TodoList>

      <CreateTodoButton />
    </>
  );
}
export default App;
