import logo from "./platzi.webp";
import "./App.css";

import { TodoCounter } from "./TodoCounter";
import { TodoFilter } from "./TodoFilter";
import { TodoList } from "./TodoList";
import { TodoItem } from "./TodoItem";
import { CreateTodoButton } from "./CreateTodoButton";

const defaultTodos = [
  { text: "Cortar Cebolla", completed: false },
  { text: "Definir Props", completed: false },
  { text: "Aprender React", completed: false },
  { text: "Elemento Sin completar", completed: false },
];

function App() {
  return (
    <>
      <TodoCounter completed={10} total={20} />
      <TodoFilter />
      <TodoList>
        {defaultTodos.map((todo) => (
          <TodoItem />
        ))}

        /* Tambiem se podría hacer de esta manera, con un retur, sin embargo se reemplazan los {} por () y no hay necesidad de hacer return, como está arriba */
        {defaultTodos.map(() => {
          return <TodoItem />;
        })}
      </TodoList>

      <CreateTodoButton />
    </>
  );
}
export default App;
