import "./TodoCounter.css";

function TodoCounter({ todos }) {
  let completados = todos.filter( elemento => elemento.completed === true);
  /* let completados = todos.filter((elemento) => {
    elemento.completed === true;
   }); */
  let completed = completados.length;
  let total = todos.length;

  return (
    <h1 className="TodoCounter">
      Has completado <span>{completed}</span> de <span>{total}</span> TODOs
    </h1>
  );
}

export { TodoCounter };
