import "./TodoItem.css";

function TodoItem({ text, completado }) {
  return (
    <p className={` ${completado && "TodoItem-p--complete"} `}>
      <span className={` ${completado && "Icon-check--active"} `}>V </span>
      {text}, Completado: {completado ? "Completado" : "Sin Completar"}
      <span className="Icon Icon-delete">X</span>
    </p>
  );
}
export { TodoItem };
